DECLARE
    num_errors PLS_INTEGER;
BEGIN
    DBMS_REDEFINITION.start_redef_table('domibus', 'TB_USER_MESSAGE', 'TMP_TB_USER_MESSAGE');

    DBMS_REDEFINITION.COPY_TABLE_DEPENDENTS('domibus', 'TB_USER_MESSAGE', 'TMP_TB_USER_MESSAGE',
                                            DBMS_REDEFINITION.CONS_ORIG_PARAMS, TRUE, TRUE, TRUE, TRUE,
                                            num_errors);

    DBMS_REDEFINITION.FINISH_REDEF_TABLE('domibus', 'TB_USER_MESSAGE', 'TMP_TB_USER_MESSAGE');

END;
/

